process.processing-script:

    InputStream is;

    controller.setUseCachedInput(true);
    controller.setDoCacheInput(true);

	is = controller.invokeInputComponent( "carrot2.input.tree-snippet-reader.google", query );

    is = controller.invokeFilterComponent( "carrot2.filter.text-only", is );

	Map lsiOptions = new HashMap();
    lsiOptions.put("stemmer.english", "com.dawidweiss.carrot.filter.stemming.porter.PorterStemmer");
    lsiOptions.put("stemmer.polish", "com.dawidweiss.carrot.filter.stemming.lametyzator.Lametyzator");
    lsiOptions.put("lsi.threshold.clusterAssignment", "0.225");
    lsiOptions.put("lsi.threshold.candidateCluster",  "0.775");
    is = controller.invokeFilterComponent( "carrot2.filter.multilingual-lsi-clusterer", is, lsiOptions );

    Map dtreeOptions = new HashMap();
    dtreeOptions.put("dtree.css", "compact");
    dtreeOptions.put("param.output.terminal.nodes", "true" );
    dtreeOptions.put("param.max.open.level", "0");
    dtreeOptions.put("param.max.subgroup.snippets.included", "3" );
    dtreeOptions.put("param.no.terminal.groups.expansion", "true" );
    dtreeOptions.put("param.snippets.sorting.type", "ORDER_SCORE_ATTRIBUTE");

    is = controller.invokeOutputComponent( "carrot2.output.xslt-renderer.dynamic-tree", is, dtreeOptions );

    controller.sendResponse( is );
